    private void DeletePressed()
    {
        if (!string.IsNullOrWhiteSpace(CurrentOperand.ToString()))
        {
            if (CurrentOperand.ToString() != "0")
            {
                CurrentOperand.Remove(CurrentOperand.Length - 1, 1);
            }
