    private void UpdateHeading(string buttonNumber)
    {
        if (!string.IsNullOrWhiteSpace(buttonNumber))
        {
            if (NewOperation == false)
            {
                if (CurrentOperand.ToString() == "0")
                {
                    if (buttonNumber == ".")
